INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21018, NULL, 322, 'На Красной площади – военнослужащие 38-й отдельной железнодорожной бригады. В годы войны воины-железнодорожники под огнем и бомбами врага обеспечивали бесперебойную доставку техники и материальных средств для нужд фронта.', 1494315480, 1, '', 0, 'Илья Максимов');
