INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22413, NULL, 341, 'Напомним, что сборная России проведет первый матч группового этапа на стадионе "Лужники". Потом команда Станислава Черчесова сыграет 19 июня на арене "Крестовский" в Санкт-Петербурге, а 25 числа - на новом стадионе в Самаре.', 1512137820, 1, '', 0, 'Елена Казьмина');
