INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22792, NULL, 346, 'Путешествие детей с тигром продолжается на олимпийском стадионе. То тут, то там на их пути возникают фантастические существа и животные.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22863, NULL, 347, 'Изменения в мире носят цивилизационный характер. И масштаб этого вызова требует от нас дать ответ. И мы готовы его дать. Чтобы идти вперед, динамично развиваться, мы должны расширить пространство свободы: укреплять институты демократии, гражданского общества, быть открытыми миру.', 1519895280, 2, '', 0, 'Дмитрий Сосновский');
