INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23331, NULL, 350, 'Впервые по брусчатке Красной площади проходит парадный расчет Военной академии войсковой противовоздушной обороны имени Маршала Советского Союза А. М. Василевского.  Возглавляет расчет начальник академии генерал-лейтенант Глеб Еремин. В годы войны за мужество и отвагу 57 воинов-зенитчиков были удостоены званий героев Советского Союза.', 1525851060, 1, '', 1, 'Илья Максимов');
