INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23894, 23892, 354, 'Трудно сказать, у нас, кажется, и прапорщиков-то больше нет. А женщин в армии все больше.', 1528374780, 4, '', 0, 'Илья Максимов');
