INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23910, 23906, 354, 'Нам сначала нужно посмотреть, как новые стадионы работают в европейской части страны. Но в названных регионах инфраструктуру тоже нужно обязательно развивать.', 1528375080, 4, '', 0, 'Дмитрий Сосновский');
