INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20948, NULL, 321, 'Москвичка Ксения Александрова смело заявляет, что "не имей 100 рублей, а имей 100 друзей", ибо дружба важнее', 1492286160, 1, '', 0, 'Никита Лашкул');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20968, NULL, 321, '"Праздник" подошел к концу. Спасибо, что запаслись терпением и провели этот вечер в компании "РГ" и красоток конкурса! До новых встреч, до свидания!', 1492289520, 1, '', 0, 'Никита Лашкул');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20970, NULL, 322, 'Доброе утро! Через 20 минут на Красной площади Москвы начнется парад, посвященный 72-й годовщине победы в Великой Отечественной войне.', 1494312000, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20971, NULL, 322, 'В столице сегодня пасмурно и прохладно, но для парада это не помеха. Девять самолетов Ан-12 и Ан-26 военно-транспортной авиации разгоняют облака и тучи над Москвой. Для этого они используют порошковые реагенты, петарды с йодидом серебра, а также сухой лед и жидкий азот. "Атакуя" тучи, военные летчики провоцируют преждевременное выпадение дождя, не позволяя осадкам добраться до центра Москвы.', 1494312300, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20972, NULL, 322, 'По словам синоптиков, вероятность дождя минимальная. Об этом сообщает <a href="http://ria.ru/" target="_blank">РИА Новости</a>.', 1494312480, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20973, NULL, 322, 'Кстати, в 1945 году Парад Победы проходил 24 июня. Дождь в тот день был настолько сильным, что воздушную часть парада пришлось отменить. 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20975, NULL, 322, 'В Параде примут участие более десяти тысяч военных, 114 единиц военной техники, некоторые образцы которой будут показаны зрителям впервые, и 72 самолета и вертолета. Самым крупным был парад по случаю 70-летнего юбилея Победы, состоявшийся в 2015 году - тогда по Красной площади прошли 16,5 тысячи солдат и офицеров, проехали 193 единицы военной техники, в том числе новейшие танки, САУ и БМП. В воздушной части Парада тогда приняли участие 143 самолета и вертолета.', 1494312720, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20976, NULL, 322, 'Красная площадь к Параду уже готова - трибуны заполнены зрителями, колонны войск приготовились к началу торжественного марша, а на Тверской улице дожидается команды боевая техника.', 1494312900, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21328, NULL, 325, 'Готовы ли вы работать с представителями оппозиции? ', 1497529260, 3, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20977, NULL, 322, 'На Красную площадь прибыл президент Российской Федерации и Верховный главнокомандующий Вооруженными силами Владимир Путин. Он прошел на трибуну, поздоровался с военными и ветеранами.', 1494312960, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20979, NULL, 322, 'Под музыку песни "Священная война" знаменные группы батальона почетного караула 154-го отдельного комендантского Преображенского полка выносят на Красную площадь государственный флаг России и знамя Победы 1945 года.', 1494313260, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20980, NULL, 322, 'Парадом командует главком Сухопутных войск генерал-полковник Олег Салюков.', 1494313320, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20981, NULL, 322, 'Принимает парад министр обороны РФ, Герой России, генерал армии Сергей Шойгу.', 1494313380, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20982, NULL, 322, 'Звучит команда "Парад, смирно!"', 1494313500, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20983, NULL, 322, '"Товарищ министр обороны Российской Федерации, войска Московского гарнизона для Парада в ознаменование 72-ой годовщины победы в Великой Отечественной войне построены", - доложил министру обороны командующий парадом.', 1494313620, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20997, NULL, 322, 'По традиции все военные парады на Красной Площади открывают барабанщики Московского военно-музыкального училища имени генерал-лейтенанта Валерия Халилова. Они же задают темп и ритм движения парадных расчетов. Командует парадным расчетом барабанщиков начальник училища - заслуженный артист России полковник Александр Герасимов. В этом году училище отмечает свой 80-летний юбилей. Напомним, что бывший художественный руководитель Академического ансамбля песни и пляски Российской армии имени А. В. Александрова Валерий Халилов, имя которого теперь носит училище, погиб 25 декабря 2016 года при крушении самолета Минобороны РФ, направлявшегося в Сирию.', 1494314700, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20984, NULL, 322, 'Автомобили командующего Парадом и министра обороны начинают объезд войск. Машины движутся абсолютно синхронно. Управляют лимузинами офицеры в звании не ниже подполковника. Как правило, это командир автобазы Минобороны и его заместители.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20985, NULL, 322, '"Здравствуйте, товарищи!", - приветствует министр военнослужащих, выполняя воинское приветствие.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20986, NULL, 322, 'Объезд войск продолжается.', 1494313800, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20987, NULL, 322, 'Завершив объезд, автомобиль министра обороны едет к трибуне для доклада президенту России Владимиру Путину.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20988, NULL, 322, '"Войска Московского гарнизона к Параду Победы готовы", - докладывает министр обороны, обращаясь к главе государства.', 1494313980, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20989, NULL, 322, 'Президент Владимир Путин поздравляет военных и ветеранов с 72-ой годовщиной Великой Победы.', 1494314040, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20990, NULL, 322, 'Уважаемые граждане России, дорогие ветераны, товарищи солдаты и матросы, сержанты и страшины, мичманы и прапорщики, товарищи офицеры, генералы и адмиралы! Поздравляю вас с Днем Победы!', 1494314100, 2, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20991, NULL, 322, 'Триумфальная победа над нацизмом навеки останется в истории вершиной торжества жизни и разума. Победа добыта ценой невосполнимых жертв. Война унесла миллионы жизней. Эту трагедию не удалось остановить из-за разобщенности ведущих стран мира. Это позволило нацистам решать судьбы народов, поставить на службу своим целям почти все европейские страны. Но нет и не будет силы, которая могла бы покорить наш народ. Он стоял насмерть и совершил невозможное - сокрушил нацизм и положил конец его зверствам.', 1494314220, 2, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20992, NULL, 322, 'Сегодня мы склоняем головы перед светлой памятью всех, кто не вернулся с войны. Перед памятью сыновей, дочерей, отцов, матерей, мужей, жен, братьев, сестер. Объявляется минута молчания.', 1494314280, 2, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20993, NULL, 322, 'Дорогие друзья, праздник 9 мая мы отмечаем по всей стране в каждой семье - ни одну из них не обошла война. Мы чувствуем кровное родство с поколением героев-победителей. Обращаясь к ним скажу: вам никогда не будет стыдно за нас. Мы будем всегда беречь Россию как это делали вы - солдаты Победы. Вооруженные силы России способны отразить любую потенциальную агрессию. Россия всегда будет на стороне сил мира, с теми, кто выбирает путь равноправного партнерства.', 1494314460, 2, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20994, NULL, 322, 'Мы обязаны передать будущим поколениям стабильность и мир на планете, передать им память о великой Победе. Чтобы в памяти навеки осталось поколение, которое с честью отстояло свое Отечество. Слава народу-победителю! С праздником, с Днем Победы!', 1494314520, 2, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20996, NULL, 322, '"Парад, смирно! К торжественному маршу, побатальонно, на одного линейного дистанции, первый батальон прямо, остальные направо, на плечо, равнение направо, шагом марш!", - командует генерал-полковник Олег Салюков. Он возглавляет торжественное шествие войск.', 1494314640, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21153, NULL, 324, 'Фестиваль открылся цитатой Даниила Дондурея. "Избегать самоцензуры - опасной защиты, убивающей любое сообщество, это наше личное дело. Наше дело - быть свободными и в текстах, и в жизни". ', 1496854140, 1, '', 1, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20998, NULL, 322, 'Мимо трибун проходят знаменные группы Знамени Победы, Государственного флага Российской Федерации и знамени Вооруженных сил России. Знаменную группу сопровождает почетный караул трех видов ВС - Сухопутный войск, Воздушно-космических сил и Военно-морского флота.', 1494314760, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (20999, NULL, 322, 'Торжественным маршем проходят самые юные участники парада - рота кадетов Московского суворовского военного училища. Расчетом командует начальник училища генерал-лейтенант Виктор Поляков.', 1494314820, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21000, NULL, 322, 'Перед трибунами проходят воспитанники образованного в 1943 году Тверского суворовского военного училища. Во главе расчета начальник училища полковник Владимир Чеховский. Изначально училище комплектовалось мальчиками в возрасте 8-13 лет из числа детей воинов Красной Армии и трудящихся, погибших в Великой Отечественной войне. За эти годы оно выпустило более 15 тысяч суворовцев. Более 70 его выпускников стали генералами.', 1494314820, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21001, NULL, 322, 'По брусчатке проходит парадный расчет Нахимовского военно-морского училища. Филиалы училища работают в Севастополе, Владивостоке и Мурманске. Многие флотские офицеры постигали военную науку в стенах этого учебного заведения. Парадный расчет возглавляет замначальника училища капитан первого ранга Анатолий Деревянко.', 1494314880, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21002, NULL, 322, 'Впервые на Красной площади парадный расчет военно-патриотического общественного движения "Юнармия". Это движение насчитывает в своих рядах более 90 тысяч юношей и девушек из всех регионов страны. Возглавляет расчет начальник Главного штаба "Юнармии" олимпийский чемпион Дмитрий Труненков.', 1494314880, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21003, NULL, 322, 'На главной площади страны - сводный полк Сухопутных войск ВС РФ.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21012, NULL, 322, 'Впервые на Красной площади парадный расчет девушек-курсантов Военной академии связи имени Буденного и Военно-космической академии имени Можайского. Выпускницы академий станут незаменимыми специалистами в области информатизации и телекоммуникаций.', 1494315300, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21163, NULL, 324, 'Ведущие вновь разлеглись на кресле и диване. Федоров вспомнил, что он - российский кинематограф, и начал отвечать на вопрос: что вы делаете для себя. "Зарабатываю деньги", - сказал он.', 1496854620, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21913, NULL, 336, '<b>Московский "Локомотив" попал в группу F, где уже есть датский "Копенгаген". </b>', 1503660360, 1, '', 0, 'Юрий Зубко');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21004, NULL, 322, 'Мимо трибун, чеканя шаг, проходит парадный расчет Военного университета Министерства обороны РФ. Парадным расчетом командует начальник университета генерал-полковник Валерий Марченков. Военный университет является крупнейшим научным центром подготовки кадров военно-гуманитарной направленности. Выпускники университета проходят службу во всех видах Вооруженных Сил, в системе Главной военной прокуратуры, военных следственных органах СК России, ГРУ, ФСБ, СВР, ФСО, работают в Государственной Думе и Совете Федерации, в МИДе и других органах власти. Более 300 выпускников вуза стали героями Советского Союза и Российской Федерации.', 1494315000, 1, '', 0, 'Илья Максимов');
