INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21013, NULL, 322, 'Ракетные войска стратегического назначения представляют офицеры и курсанты Военной академии имени Петра Великого - одного из старейших учебных заведений страны. Возглавляет парадный расчет начальник академии генерал-лейтенант Сергей Сивер. Сегодня на вооружении РВСН стоят не имеющие аналогов ракетные комплексы, обладающие высокой степенью боевой готовности и надежности. Многие выпускники академии прославились в боях за свободу и независимость Родины. Среди них 193 кавалера военного ордена св. Георгия, 128 Героев Советского Союза.', 1494315360, 1, '', 0, 'Илья Максимов');
