INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21023, NULL, 322, 'На Красной площади - военнослужащие 4-й гвардейской танковой Кантемировской дивизии имени Андропова. Парадный расчет возглавляет командир дивизии гвардии полковник Андрей Колесников. В годы войны воинские части соединения награждены 23 орденами, личному составу корпуса 18 раз объявлялась благодарность Верховного Главнокомандующего, 32 военнослужащим присвоено звание Героя Советского Союза, пятеро воинов дивизии стали полными кавалерами ордена Славы.', 1494315660, 1, '', 0, 'Илья Максимов');
