INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21167, NULL, 324, 'Теперь Роднянский представляет конкурс. Отмечает тенденции: это и омоложение кинематографа, и активное участие регионов. По его словам, значительная часть фильмов снята без госфинансирования. А две картины - копродукция - сделаны сразу несколькими странами. ', 1496854980, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21168, NULL, 324, '"Мы пытаемся предложить зрителям кинематограф свежий и разнообразный", - сказал Роднянский.', 1496855040, 1, '', 0, 'Андрей Полухин');
