INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21172, NULL, 324, 'Чухрай рассказывает, что когда-то его спросили, чего бы он больше хотел: зарабатывать деньги или делать то, что ему нравится? И сказал о том, что это трудно совместить. У нашего кинематографа та же проблема, констатировал режиссер.', 1496855520, 1, '', 1, 'Андрей Полухин');
