INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21330, NULL, 325, 'Алексей Георгиевич, директор школы, Краснополка, Ульяновская область: В РФ отсутствует единое образовательное пространство. При переезде многие ученики испытывают трудности. И еще один вопрос. Срок эксплуатации учебников ограничен пятью годами. За это время учебники не успевают исчерпать свой ресурс, а на закупку новых тратятся огромные средства. Что с этим делать?', 1497529440, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21377, NULL, 326, 'Сейчас в ГЦКЗ "Россия" идут предварительные бои. Коротко расскажем об их итогах, затем познакомим вас с тем, как пройдет поединок между экс-чемпионом мира Эдуардом Трояновским и итальянским боксером Ди Рокко, ну а затем все внимание переключим на Александра Поветкина, которому предстоит биться с украинцем Андреем Руденко.', 1498930500, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21332, NULL, 325, 'Краснополка, Ульяновская область: Мы выпускники 9 класса. Участвуем в конкурсе "Выпускной-2017". Не могли бы вы пожелать нам удачи и сказать напутственные слова?', 1497529740, 3, '', 1, 'Илья Максимов');
