INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21336, NULL, 325, 'Данила: Коррумпированные чиновники и министры - это давно не новость. Показательные посадки и аресты результатов не дают. Все это подрывает доверие граждан. Что вы планируете с этим делать? ФЗ гласит, что каждый сотрудник полиции может получить средства на приобретение жилья. В ХМАО эти средства выплатили всего 90 семьям, очередь почти не двигается.', 1497530040, 3, '', 1, 'Илья Максимов');
