INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21368, NULL, 325, 'И наконец, не вопрос, а утверждение, я сейчас прочитал на экране: "Все будет хорошо".', 1497531420, 2, '', 0, 'Дмитрий Сосновский');
