INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21494, NULL, 327, 'В последнем раунде Флэнаган действовал не столь решительно, как в двух предыдущих. Было впечатление, что он пытался улучить момент для сильного и мощного удара, который может принести ему победу в проигранном в целом бою. Но Денис особых шансов сопернику не предоставил, выдерживая приемлемую дистанцию, уходя из-под ударов и время от времени проводя свои атаки. В итоге досрочной победы чемпиона не случилось, но пояс он сохранил без видимого напряжения, хотя и без блеска. ', 1499631240, 1, '', 0, 'Андрей Полухин');
