INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21497, NULL, 327, '"В его глазах я увидел полную концентрацию, это был его шанс, - напомнил Лебедев в интервью в ринге, объясняя свою осторожность в течение всего боя.', 1499631720, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21498, NULL, 327, '"В принципе сегодня сработал мой план полностью - от начала до конца", - ответил Денис на вопрос о том, удовлетворен ли он тем, как прошел бой.', 1499631780, 1, '', 0, 'Андрей Полухин');
