INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21543, NULL, 329, 'Добрый день, дорогие друзья. У вас заканчивается смена и учебный год. Разговор приурочен к этим событиям, но я хотел бы вернуться к началу и вспомнить о том, как возник проект "Сириус". Вы знаете, что в странах, где проходят крупные международные соревнования, всегда встает вопрос о том, что делать с сооружениями после их проведения. Такой стоял и перед нами. Сочи нужно было сделать крупным международным курортом, но мне хотелось добавить к этому и кое-что еще. Конечно, это касалось спорта: хоккей, фигурное катание. А потом у меня возникла мысль: почему только спорт? У нас есть и другие сферы, которыми мы гордимся. В том числе - специализированные учебные заведения, физико-математические, химические школы, потом возникли биологические классы. И постепенно я пришел к выводу, что нужно создать комплексный центр. Поделился идеями с коллегами в Москве. Компетентные люди сказали: "Ничего у вас не получится". Я спросил: "Почему?". Они ответили: "Потому что ваши хоккеисты начнут с того, что набьют морды этим математикам". Я сначала напрягся, но потом пообщался с выдающимися хоккеистами, и они сказали: "Ничего такого не будет. Потому что спортсмены знают цену победы". По мнению моей преподавательницы, именно так все и сложилось. Проект удается. И я хочу вас с этим поздравить, потому что это - ваша заслуга.', 1500643080, 2, '', 1, 'Дмитрий Сосновский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21547, NULL, 329, 'Артем: Конечно, я с вами согласен. Вопрос у меня не научный. Я - активный пользователь интернета. А вы? Пользуетесь ли вы соцсетями?', 1500643200, 3, '', 0, 'Дмитрий Сосновский');
