INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21816, NULL, 334, 'Андрей Григорьев, Екатеринбург: Мы экранизировали судьбу Николая Васенина, это герой войны. Нас поддержали деятели культуры и искусства, деньги собирали с миру по нитке. Фильм показали в 57 городах, получили президентский грант. Это мой третий фильм, я его закончил. Сейчас я делаю художественный фильм о Перельмане. 8 июня удалось встретиться с ним лично, это большая удача. Удалось многое, переходим на производственную стадию. Мы хотели попросить вас написать рекомендательное письмо в Министерство культуры. Также просим вас прийти на премьеру и поддержать наш фильм.', 1503245400, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21818, NULL, 334, 'Даниил Чащин, Тюмень: Речь о возможности получить бесплатное режиссерское образование тем, кто уже получил высшее образование. Эта профессия требует опыта. Сегодня много одаренных людей с высшим образованием, но без 300 тысяч режиссерами они стать не могут. Искусство строится на личности. Можно ли как-то запустить механизм решения этой проблемы через систему президентских грантов?', 1503245580, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21872, NULL, 335, '<b>"Севилья" отправляется в группу E к "Спартаку"</b>', 1503592320, 1, '', 1, 'Елена Казьмина');
