INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21820, NULL, 334, 'Алексей Капустин: Год назад мы показывали вам проект по восстановлению Чудова и Воскресенского монастырей, с вашей помощью получили грант. Мы подходили к проекту как к возрождению высокой национальной культуры русской цивилизации. Вы говорили, что необходимо придать проект огласке. Мы давали интервью, было два фильма по телевидению. Мы написали книгу, хотел бы вам ее передать. Мы показали проект патриарху, он его поддержал. Проект также стал инструментом "мягкой силы" - правительство Сербии приняло решение построить храм, посвященный семье Романовых, в столице республики. Мы хотели бы и дальше работать по проекту и продемонстрировать его общественности в полной мере. Я верю, что на этом проекте вырастет новое поколение архитекторов.', 1503245880, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21822, NULL, 334, 'Светлана Козинова, дизайнер одежды: Я три года шла к встрече с вами, чтобы поблагодарить за мирное небо над головой. Никто не создает для нас красивую и удобную одежду. И никто не учитывает наше мнение. Мы пришли к выводу,что ситуацию надо менять, и создали проект, в рамках которого инвалиды создадут удобную одежду.', 1503246060, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21824, NULL, 334, 'Валерия, Ярославль: Вопрос по поводу вашего стиля. Как вы рассматриваете свой стиль, как его видите? Как он эволюционировал?', 1503246180, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21826, NULL, 334, 'Георгий Федоров, Санкт-Петербург: Я художественный руководитель симфонического оркестра. Мне 22 года. У нас камерный оркестр. Я организовал миссию со своим оркестром. Благодаря "Тавриде" мы получили грант на развитие и собираемся устраивать концерты классической музыки, направленные на неподготовленную молодежную аудиторию. Хотелось бы расширить этот проект, создать такие оркестры в других городах. Чтобы вовлекать публику в мир классики. Это большое дело и большая ответственность. Вы - руководитель самого большого оркестра в мире, по площади уж точно. Расскажите, каково это.', 1503246480, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21688, NULL, 332, 'Министр обороны РФ Сергей Шойгу проходит мимо команд-участниц.', 1501319760, 1, '', 1, 'Николай Грищенко');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21689, NULL, 332, 'Выглянуло солнце. Офицеры иностранных армий продолжают рассаживаться перед сценой. Военный оркестр исполняет попурри из мелодий стран-участниц.', 1501320240, 1, '', 0, 'Николай Грищенко');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21839, NULL, 334, 'Александр Антипов, Москва: Сейчас интересное время для поэтов. В 90-е и в начале 2000-х было не до этого, а сейчас я вижу подьем. Говорят даже о начале нового века русской поэзии. Мы хотим, чтобы нас слышали. Мы три раза проводили фестиваль в честь Леонида Филатова, победители получают возможность напечатать книгу и поехать на "Тавриду". Сейчас мы создаем "Есенин центр". Мы хотим, что это была точка притяжения для всех молодых поэтов. Мы не имеем права потерять новое поколение поэтов. Я благодарен вам за внимание к пишущим людям. Приглашаем вас посетить "Есенин центр" в Москве.', 1503247800, 3, '', 1, 'Илья Максимов');
