INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21828, NULL, 334, 'Тихон Хренников: Спасибо, что любите творчество моего прадеда - Тихона Хренникова. Для композитора самое важное - это исполнение его музыки. Зачастую молодые авторы сталкиваются с проблемой неисполнения их музыки. Мы сделали конкурс для композиторов на написание симфонического произведения. Нам прислали более 20 партитур, три лучших произведения исполнили на последнем концерте смены. Есть ли возможность ввести квоты на исполнение произведений молодых композиторов? Создается много талантливой музыки, но ее не знают.', 1503246780, 3, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21830, NULL, 334, 'Мария Молчанова: Форум "Таврида" поддерживает Международный фестиваль молодежи и студентов. Всемирные фестивали молодежи, походившие в СССР, оказали большое влияние на страну. Чего стоит ожидать от фестиваля этого года?', 1503247020, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21832, NULL, 334, 'Дмитрий Акимов, Иркутск: В "Тавриду" приезжают тысячи талантливых молодых людей со всей страны. С нами работают ведущие эксперты и педагоги. Обратно мы возвращаемся потенциальными руководителями учреждений культуры. Я знаю, что на базе ОНФ организуется молодежное направление. Может, можно создать в рамках него молодежный комитет по культуре? Который советовал бы тех или иных выпускников "Тавриды".', 1503247260, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21834, NULL, 334, 'Анастасия Безрук: У меня болит душа за документальное кино, которое не видят зрители нашей страны. Кинотеатрам нас показывать не выгодно. Не могли бы вы помочь доносить кино до зрителя? У нас много предложений.', 1503247380, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21836, NULL, 334, 'Яна, Крым: "Таврида" изменил мою жизнь. Мы недавно проводили с детьми мастер-класс по созданию игрушек. Это дети из детдомов Крыма. Они сделали вам подарок - тигренка. Хотели бы подарить вам его от лица детей, чтобы он стал вашим талисманом.', 1503247500, 3, '', 1, 'Илья Максимов');
