INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (21841, NULL, 334, 'Александр Шкаленко, Тюмень: Вопрос касается детей. Вы сами говорили про общечеловеческие ценности. Но поп-культура ведет к обратному, она действует с обратной силой - мы видим убийства, искажение роли семьи и так далее. Мы решаем этот вопрос, организуем международный кинофестиваль 0+ для фильмов, которые популяризируют общечеловеческие ценности. Также у нас есть передвижной кинотеатр для показа этих фильмов. Можно ли создать механизм, который будет фильтровать поп-культуру? И еще. Моя бабушка вас очень поддерживает, привет вам от нее.', 1503248160, 3, '', 1, 'Илья Максимов');
