INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22104, NULL, 338, 'Несмотря на то, что весь день погода была идеальной для прогулок, на улице начинает холодать. К девяти часам вечера температура воздуха опустится до +14 градусов. Одевайтесь теплее, чтобы ничто вам не мешало смотреть салют.', 1504973940, 1, '', 0, 'Полина Иваницкая');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22105, NULL, 338, 'На Тверской продолжаются гуляния: судя по неиссякающему потоку людей, интерес к космосу у горожан по-прежнему велик.', 1504974300, 1, '', 1, 'Полина Иваницкая');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22106, NULL, 338, 'Для тех, кто подзабыл школьный курс астрономии - есть шанс освежить знания и изучить макет солнечной системы. Вечером даже лучше - смотрите, какая подсветка экспонатов!', 1504974300, 1, '', 1, 'Полина Иваницкая');
