INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22110, NULL, 338, 'Бесплатные экскурсии по Тверской проходят весь день. Последняя экскурсия в 20.45. Поторопитесь, у вас ещё есть шанс успеть! Экскурсоводы совершенно бесплатно проведут вас по Тверской, где выставлены макеты знаменитых высоток Москвы, и расскажут про каждую из них.', 1504977840, 1, '', 1, 'Полина Иваницкая');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22111, NULL, 338, 'Для любителей науки через пару минут прямо у стен московской мэрии начнётся познавательная лекция "Химия мозга". Послушать о том, как функционирует наш мозг, собрались и взрослые, и дети. Присоединяйтесь!
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22112, NULL, 338, 'А на соседнем стенде музей занимательных наук "Экспериментариум" проводит зрелищные химические опыты.', 1504978440, 1, '', 1, 'Полина Иваницкая');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22113, NULL, 338, 'Радостная новость для тех, кто сегодня не успел на все опыты, эксперименты и лекции - программа продолжится завтра. С 9 до 18  - все для вас!', 1504978560, 1, '', 1, 'Полина Иваницкая');
