INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22186, NULL, 339, 'Часы научатся отслеживать новые типы активности.', 1505237520, 1, '', 0, 'Антон Благовещенский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22534, NULL, 342, 'Остается узнать жребий ЦСКА. А пока выяснилось, что "Наполи" сыграет в следующем раунде с "Лейпцигом". Еще одно немецко-итальянское противостояние на этой стадии.', 1512994920, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22159, NULL, 339, 'Тим Кук проснулся (а может, и не спал всю ночь) и твитит. Говорит, что это большой день для Apple, и они очень горды и рады, что первое представление состоится в Театре Стива Джобса.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22161, NULL, 339, 'Кто-то переживает, что через восемнадцать минут его новый айфон превратится в тыкву.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22162, NULL, 339, '<blockquote class="twitter-tweet" data-lang="ru"><p lang="ru" dir="ltr">скоро выйдет новый <a href="https://twitter.com/hashtag/iPhone8?src=hash">#iPhone8</a>, поэтому лучше не заходить в твиттер до завтра </p>&mdash; Владимир (@san_gig) <a href="https://twitter.com/san_gig/status/907641410794475520">12 сентября 2017 г.</a></blockquote>
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22163, NULL, 339, 'Те, кому неохота читать текстовую трансляцию, могут наблюдать за событием в режиме реального времени на <a href="https://www.apple.com/apple-events/september-2017/" target="_blank">сайте Apple</a>. Понадобится браузер Safari или Microsoft Edge.', 1505234820, 1, '', 0, 'Антон Благовещенский');
