INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22170, NULL, 339, 'Не забыты и жертвы урагана Ирма. Пожертвовать им деньги можно через iTunes и App Store.', 1505236080, 1, '', 0, 'Антон Благовещенский');
