INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22176, NULL, 339, 'На сцене снова Тим Кук. Рассказывает про Apple Watch.', 1505236740, 1, '', 0, 'Антон Благовещенский');
