INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22481, NULL, 341, '<blockquote class="twitter-tweet" data-lang="ru"><p lang="ru" dir="ltr">Традиционное фото тренеров команд-участниц Ч2018 <a href="https://t.co/zSklDIe26c">pic.twitter.com/zSklDIe26c</a></p>&mdash; Сборная России (@TeamRussia) <a href="https://twitter.com/TeamRussia/status/936629316196151296?ref_src=twsrc%5Etfw">1 декабря 2017 г.</a></blockquote>
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22482, NULL, 341, 'Вратарь сборной России Андрей Лунев положительно оценил жеребьевку группового турнира чемпионата мира-2018. В интервью "Спорт-экспрессу" он заявил следующее: "Жеребьевка - супер".
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22483, NULL, 341, 'К сожалению, до сих пор никто не вышел в микст-зону, чтобы пообщаться с журналистами. Поэтому не будем вас задерживать - сегодня все-таки пятница. Комментарии тренеров вы сможете прочитать чуть позже в разделе "спорт" на нашем сайте. Спасибо большое за внимание! До новых встреч!', 1512146100, 1, '', 0, 'Юрий Зубко');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22484, NULL, 342, 'Добрый день, уважаемые любители футбола! Мы начинаем нашу трансляцию.', 1512988260, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22485, NULL, 342, 'До самой церемонии жеребьевки 1/8 финала Лиги чемпионов еще полчаса, но за это время у нас есть возможность узнать все последние новости, которые касаются этого события!', 1512988320, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22486, NULL, 342, 'Итак, для начала напомним, что сеяными при жеребьевке будут победители групп в квалификации: это "Манчестер Юнайтед", "Пари Сен-Жермен", "Рома", "Барселона", "Ливерпуль", "Манчестер Сити", "Бешикташ" и "Тоттенхэм". Совершенно неожиданно турецкий клуб оказался в этой компании. По всей видимости, именно с ним желают встретиться клубы, оказавшиеся в другой корзине: "Базель", "Бавария", "Челси", "Ювентус", "Севилья", "Шахтер" и "Реал". С "Порту" стамбульцы уже играли на предыдущем этапе, так что их жребий уже точно не сведет.', 1512988740, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22487, NULL, 342, 'Любопытное голосование идет на русскоязычной странице Лиги чемпионов в соцсети "ВКонтакте": пользователи выбирают, какую пару они больше всего хотят увидеть в 1/8 финала. Почти треть из 16 тысяч голосов отдано в пользу матча "ПСЖ" - "Реал". За ним идут такие поединки, как "Барселона" - "Бавария" и "Реал" - "МЮ".', 1512988920, 1, '', 0, 'Андрей Полухин');
