INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22495, NULL, 342, 'Кстати, ранее предполагалось, что шары будет доставать посол финального матча (он пройдет в мае в Киеве) Андрей Шевченко, которого, конечно, российским любителям футбола представлять не нужно.', 1512990480, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22496, NULL, 342, 'Алонсо дважды выиграл Лигу чемпионов - в составе "Ливерпуля" и мадридского "Реала". А еще ему небезразлична судьба "Баварии", за которую он также поиграл.', 1512990600, 1, '', 0, 'Андрей Полухин');
