INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22415, NULL, 341, 'Главный тренер сборной России Станислав Черчесов наверняка волнуется, хотя вида старается не подавать. Пока он мило общается с Дмитрием Сенниковым, который получил приглашение в качестве легенды ФИФА.', 1512137880, 1, '', 0, 'Юрий Зубко');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22499, NULL, 342, 'Аллегри угадал: "Ювентусу" предстоит сыграть против "Тоттенхэма".', 1512990840, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22500, NULL, 342, 'Вторая пара: "Базель" - "Манчестер Сити". Здесь фаворит очевиден, хотя швейцарцы на групповом этапе на своем поле смогли обыграть другой клуб из Манчестера.', 1512990900, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22501, NULL, 342, '"Порту" сыграет с "Ливерпулем".', 1512990960, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22502, NULL, 342, 'Удивительно: Хаби Алонсо в первых четырех попытках достал шары только с английскими командами. "Севилья" сыграет с "Манчестер Юнайтед".', 1512991020, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22503, NULL, 342, 'Мадридский "Реал" сыграет с "Пари Сен-Жермен" - вот это пока самая интересная пара!', 1512991020, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22504, NULL, 342, 'Донецкий "Шахтер" на пути к киевскому финалу сразится с "Ромой".', 1512991080, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22505, NULL, 342, '"Челси" ждет соперника: "Бешикташ" или "Барса"?', 1512991080, 1, '', 0, 'Андрей Полухин');
