INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22818, NULL, 346, 'И все-таки он это сделал. Пита Николас Тауфатофуа из Тонга - несмотря на погоду - вышел опять в своем "костюме".', 1518178080, 1, '', 1, 'Анастасия Комарова');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23694, NULL, 353, 'Албанцы отдали 12 очков Италии', 1526162760, 1, '', 0, 'Дмитрий Сосновский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22508, NULL, 342, 'Еще раз о том, что в 1/8 финала Лиги чемпионов у нас будет две невероятно интересных пары, которые вполне могли бы украсить полуфинальный раунд турнира: 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22512, NULL, 342, 'Пожалуй, можно порадоваться тому, что откровенно слабых пар (уровня "Базель" - "Бешикташ") не будет, при этом два противостояния болельщики будут с нетерпением ждать до февраля.', 1512991680, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22850, NULL, 346, 'Над стадионом продолжается салют, а мы констатируем - церемония открытия Олимпиады в Пхенчхане подошла к концу. Спасибо, что были сегодня с нами! Следите за новостями Олимпийских игр на сайте "РГ"!', 1518182580, 1, '', 0, 'Анастасия Комарова');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22514, NULL, 342, 'Все 12 предыдущих матчей между "Челси" и "Барселоной" были сыграны в рамках Лиги чемпионов УЕФА. В активе лондонцев четыре победы, каталонцы выиграли три. Последняя встреча пришлась на полуфинал-2011/12. Дома "Челси" отпраздновал минимальную победу, а затем добыл результативную ничью на "Камп Ноу" - 2:2. В итоге лондонский клуб завоевал трофей, напоминает официальный сайт УЕФА.', 1512991860, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22515, NULL, 342, 'А "Реал" и "ПСЖ" встречались между собой в еврокубках шесть раз. Команды одержали по две победы, еще два матча завершились вничью.', 1512991920, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22516, NULL, 342, 'Напомним еще, что "Шахтер" уже выбивал "Рому" из Лиги чемпионов - в сезоне 2010/2011, а в этом году отправил "Наполи" в Лигу Европы. И на этом, пожалуй, пора переходить к предстоящей жеребьевке как раз в этом турнире. Тем более что своих соперников узнают сразу 4 наших команды.', 1512992580, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22517, NULL, 342, 'Итак, "Зенит", ЦСКА и "Локомотив" у нас среди "сеяных" клубов, "Спартак" - во второй корзине. Он не сможет сыграть с этими тремя клубами, а также с киевским "Динамо". Следовательно, среди его соперников могут быть "Арсенал", "Аталанта", "Атлетик" и "Атлетико", "Брага", "Виктория", "Вильярреал", "Зальцбург", "Лацио", "Лейпциг", "Милан" и "Спортинг". Очевидно, что есть варианты попроще, а есть очень даже сложные.', 1512993240, 1, '', 0, 'Андрей Полухин');
