INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22520, NULL, 342, 'Жеребьевка в Ньоне начинается с приятного: УЕФА поздравила самый результативный клуб группового этапа - "Зенит"!', 1512993720, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22521, NULL, 342, 'Финал Лиги Европы состоится в мае в Лионе - и у местного клуба есть шанс посмотреть этот матч не с трибун. Он тоже среди участников 1/16 финала.', 1512993900, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22522, NULL, 342, 'Шары на жеребьевке Лиги Европы будет вытаскивать Эрик Абидаль - французский футболист "Лиона" и "Барселоны", дважды выигравший Лигу чемпионов и - главное - победивший рак. Эта победа гораздо важнее всех остальных!', 1512994200, 1, '', 0, 'Андрей Полухин');
