INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22551, NULL, 342, 'Президент ФК "Локомотив" Илья Геркус в телеинтервью уже назвал жребий своего клуба интересным. "Железнодорожники" не боятся клуб Марио Балотелли, но подготовиться к игре им надо очень серьезно, сказал руководитель клуба.', 1512996000, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22552, NULL, 342, '"Достаточно неплохой жребий, все в наших руках, - так оценил предстоящее соперничество с "Атлетиком" вратарь "Спартака" Артем Ребров. - Да, это испанцы, но мы уже играли с "Севильей" и обыграли ее дома".', 1512996120, 1, '', 0, 'Андрей Полухин');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22553, NULL, 342, '"Црвена Звезда" — практически "Спартак". Будем готовиться как к дерби", - сказал журналистам форвард "армейцев" Федор Чалов. Хм, пожалуй, ЦСКА надо готовиться к встрече с сербами лучше, чем к дерби!', 1512996240, 1, '', 0, 'Андрей Полухин');
