INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22554, NULL, 342, '<blockquote class="twitter-tweet" data-cards="hidden" data-lang="ru"><p lang="ru" dir="ltr">Следующая станция - Ницца. Интересный соперник, именитый клуб, теплый климат и красивый город. Это будет очень интересное противостояние. Балотелли, держись! <a href="https://twitter.com/hashtag/%D0%9B%D0%BE%D0%BA%D0%BE%D0%9A%D0%B0%D1%82%D0%B8%D1%82?src=hash&amp;ref_src=twsrc%5Etfw">#ЛокоКатит</a> <a href="https://twitter.com/hashtag/%D0%9B%D0%BE%D0%BA%D0%BE%D0%BC%D0%BE%D1%82%D0%B8%D0%B2?src=hash&amp;ref_src=twsrc%5Etfw">#Локомотив</a> <a href="https://twitter.com/hashtag/%D0%96%D0%B5%D0%BB%D0%B5%D0%B7%D0%BD%D0%BE%D0%B2%D0%BC%D0%B5%D1%81%D1%82%D0%B5?src=hash&amp;ref_src=twsrc%5Etfw">#Железновместе</a> <a href="https://twitter.com/hashtag/%D0%AF%D1%80%D1%87%D0%B5%D0%9F%D1%80%D0%BE%D1%88%D0%BB%D0%BE%D0%B3%D0%BE?src=hash&amp;ref_src=twsrc%5Etfw">#ЯрчеПрошлого</a> <a href="https://twitter.com/hashtag/%D0%98%D0%BB%D1%8C%D1%8F%D0%93%D0%B5%D1%80%D0%BA%D1%83%D1%81?src=hash&amp;ref_src=twsrc%5Etfw">#ИльяГеркус</a> <a href="https://twitter.com/hashtag/%D0%A4%D0%9A%D0%9B%D0%BE%D0%BA%D0%BE%D0%BC%D0%BE%D1%82%D0%B8%D0%B2?src=hash&amp;ref_src=twsrc%5Etfw">#ФКЛокомотив</a> <a href="https://twitter.com/hashtag/%D0%A1%D0%B0%D0%BC%D1%8B%D0%B9%D0%9B%D1%83%D1%87%D1%88%D0%B8%D0%B9%D0%9A%D0%BE%D0%BB%D0%BB%D0%B5%D0%BA%D1%82%D0%B8%D0%B2?src=hash&amp;ref_src=twsrc%5Etfw">#СамыйЛучшийКоллектив</a> <a href="https://twitter.com/hashtag/%D0%A2%D0%BE%D0%BB%D1%8C%D0%BA%D0%BE%D0%9B%D0%BE%D0%BA%D0%BE?src=hash&amp;ref_src=twsrc%5Etfw">#ТолькоЛоко</a> <a href="https://t.co/MRqDZj4E7a">pic.twitter.com/MRqDZj4E7a</a></p>&mdash; Илья Геркус (@ilgerkus) <a href="https://twitter.com/ilgerkus/status/940198922844360704?ref_src=twsrc%5Etfw">11 декабря 2017 г.</a></blockquote>
