INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22669, NULL, 343, 'Михаил Зуб, издательство "Потребитель": Мы вам благодарны за принятие идеологии о том, что рыба - это национальное достояние. Но выходит закон, который делает "обрезание" вашей идеологии.', 1513253100, 3, '', 1, 'Дмитрий Сосновский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22665, NULL, 343, 'Александр Гамов, КП: Самый главный политический вопрос сегодня, согласитесь, - это здоровье нации. Мы сдвинулись далеко вперед в этом плане. Появились медицинские центры в глубинке, о которых мы даже не мечтали. Но параллельно с этим прогрессом началась так называемая оптимизация. И это просто катастрофа. Начали укрупнять учреждения, закрывать акушерские пункты. Когда дорог нет, авиации нет, это катастрофа. Все привязано к деньгам и экономической обоснованности. Все так, мне кажется, здесь перегнули палку, и эта изнанка ужасна. И еще, может быть, заглянете к нам в редакцию?
