INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22667, NULL, 343, 'Татьяна Фельгенгауэр, "Эхо Москвы": У меня вопрос о верховенстве права. Мы видим две разные правовые реальности. С одной стороны - репрессивная машина, в которой сидят за соцсети, и Олег Навальный. Мы видим другую, в ней убит Борис Немцов, а по делу Улюкаева важнейший свидетель - Сечин - не явился в суд. О каком верховенстве права можно говорить, если мы имеем две разные правовые реальности?', 1513252800, 3, '', 1, 'Дмитрий Сосновский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22773, NULL, 346, 'Вот, например, совсем недавно возможность пробежать с олимпийским факелом в руке получил Джеки Чан. Вряд ли он нуждается в особом представлении.', 1518169260, 1, '', 0, 'Анастасия Комарова');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22671, NULL, 343, 'Ксения Собчак, ТК "Дождь": Вопрос про конкуренцию на выборах. Оппозиционных кандидатов либо не пускают на выборы, либо создают им проблемы. Это я знаю по себе. Есть пример Алексея Навального, против которого созданы фиктивные дела. Почему так происходит? Неужели власть боится честной конкуренции?', 1513253400, 3, '', 1, 'Дмитрий Сосновский');
