INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22800, NULL, 346, 'А вот Норвегия. Их знаменосец - биатлонист Эмиль Хегле Свендсен. Как минимум еще одного биатлониста-знаменосца мы увидим сегодня.', 1518175740, 1, '', 0, 'Анастасия Комарова');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22685, NULL, 343, 'Зарина Атаева, телеканал Грозный: Кадыровым проводится огромная работа по вызволению граждан РФ с территорий, контролируемых террористами. В последнее время появились "эксперты", которые усомнились в целесообразности такой работы. Что вы думаете по этому поводу?', 1513255080, 3, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22687, NULL, 343, 'Сергей Мильвит, Примамедиа: Хотелось бы попросить вас оказать содействие в сохранении лесозащитных зон Владивостока.  Также просим посодействовать в вопросе строительства спорткомплекса для детей. Стройка была заморожена.', 1513255260, 3, '', 1, 'Илья Максимов');
