INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22690, NULL, 343, 'Хочу поздравить всех с наступающим Новым годом! Желаю всего наилучшего, благополучия вам и вашим близким. Всего доброго всей нашей стране. Мы вас слышим, мы высоко оцениваем роль и значение российской прессы в жизни страны.', 1513255800, 2, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22693, NULL, 344, 'Доброе утро! Совсем скоро в Волгограде начнутся торжественные мероприятия, посвященные Дню воинской славы России. Сталинградская битва (17 июля 1942 года – 2 февраля 1943 года) по масштабности является одной из крупнейших во Второй мировой войне. Это сражение стало предвестником победы 9 мая 1945 года.', 1517553060, 1, '', 0, 'Никита Лашкул');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22695, NULL, 344, 'Торжественные мероприятия начались и в других городах России. В Черкесске более 200 школьников и студентов приняли участие в прохождении исторического квеста "Сталинградская битва". Игрокам пришлось попробовать себя в роли журналистов и собрать подлинные исторические факты.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22696, NULL, 344, 'Раньше всех праздничные мероприятия начались на Дальнем Востоке. В амурском краеведческом музее Благовещенска прошел исторический квест "Сталинградская битва". В его основу легли истории и факты из видеоархива воспоминаний ветеранов Великой Отечественной войны. Участники преодолели шесть дистанций  -  отгадывали координаты наступления, демонстрировали знания об оружии и технике времен войны. Одним из самых сложных заданий оказалось строительство моста.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22697, NULL, 344, 'В Волгограде собравшиеся застыли в минуте молчания, чтобы почтить память погибших во время войны.', 1517554800, 1, '', 1, 'Анна Петяева');
