INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22819, NULL, 346, 'Мартен Фуркад несет флаг сборной Франции. Уже давно было известно, что он будет знаменосцем своей страны. Пожалуй, сложно поспорить с выбором его кандидатуры - от него ждут медалей.', 1518178200, 1, '', 0, 'Анастасия Комарова');
