INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22720, NULL, 344, 'Государственный архив Бурятии открыл сегодня <a href="http://gbu-garb.ru/%D1%8D%D0%BB%D0%B5%D0%BA%D1%82%D1%80%D0%BE%D0%BD%D0%BD%D1%8B%D0%B5-%D0%B2%D1%8B%D1%81%D1%82%D0%B0%D0%B2%D0%BA%D0%B81" target="_blank">электронную выставку</a> документов об участии жителей республики в Сталинградской битве. 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22721, NULL, 344, 'В Самаре прошел музыкальный флешмоб - танцоры и участники команды "Молодежка ОНФ" провели акцию "Память в танце. Случайный вальс" в самарском областном геронтологическом центре. Пожилых самарцев, которые проживают в пансионате, пригласили присоединиться к вальсу в память о защитниках Сталинграда.  
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22722, NULL, 344, 'Глава Крыма Сергей Аксенов назвал Сталинградскую битву символом доблести наших воинов.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22723, NULL, 344, 'В Краснодарском художественном музее имени Ф. А. Коваленко открылась выставка, посвященная празднованию 75-летия победы в Сталинградской битве и 75-й годовщине освобождения Кубани от немецко-фашистских захватчиков. В экспозиции представлены 35 произведений живописи, графики и скульптуры, а также 27 архивных фотографий.', 1517561100, 1, '', 1, 'Анна Петяева');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22738, NULL, 345, 'А тем временем, пока идет закулисная борьба, началась борьба и реальная. Как обычно, в отдельных видах соревнования начинаются еще до открытия Олимпиады. И первыми из наших атлетов стартовали на Играх керлингисты.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (24124, NULL, 356, 'Никаких провалов в тенях и засветок, потрясающая детализация, все дела, точная передача оттенков кожи.', 1536775860, 1, '', 0, 'Антон Благовещенский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22724, NULL, 344, 'В Анапе в преддверии юбилея в Доме культуры станицы Анапской чествовали ветерана Великой Отечественной войны, участницу Сталинградской битвы Клавдию Виноградову.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22851, NULL, 347, 'Гости занимают свои места в ожидании начала оглашения Послания Федеральному Собранию.', 1519894020, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22725, NULL, 344, 'В Петербурге в рамках фестиваля "Артдокфест" представили документальную ленту о Сталинградской битве "Сталинград. Мы еще живы или нет?". Полуторачасовой фильм режиссера Алексея Янковского создавался три года. В его основу легли кадры, выбранные из 6 тысяч часов кинохроники и воспоминаний людей, остававшихся в городе. В Волгограде ленту покажут в феврале, на заводе "Баррикада".
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22726, NULL, 344, 'В екатеринбургском мультимедийном историческом парке "Россия - моя история" открылась выставка военных артефактов, экспонатов и находок, обнаруженных на полях сражений военными археологами, участниками военно-исторических клубов "Горный Щит" и "Урал-фронт". 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22727, NULL, 344, 'В Объединенном государственном архиве Челябинской области школьникам прочтут вариант стихотворения поэта Сергея Михалкова "Посылка", которое было написано в годы войны специально для уральцев. 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22728, NULL, 344, 'Корреспонденты "Родины" побывали у 100-летнего ветерана Николая Кравца. Он рассказал, как начиналась битва за <a href="https://rg.ru/2018/02/02/rodina-kravec-stalingrad.html" target="_blank">Сталинград</a>.
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22729, NULL, 344, 'В Ярославле памятные мероприятия прошли у мемориала "Вечный огонь". Участники возложили цветы и почтили память героев минутой молчания. В Ярославской области сегодня проживают 17 участников Сталинградской битвы.', 1517563080, 1, '', 1, 'Ася Кушнарева');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22730, NULL, 344, 'В Саратове митинг памяти проходит на Соколовой горе в парке Победы. В нем участвуют представители ветеранских и молодежных организаций, школьники. 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22731, NULL, 344, 'В это время в Волгограде начала свое выступление группа из шести боевых истребителей МиГ-29. Легендарные "Стрижи" показывают фигуры высшего пилотажа, летая над Волгой. Тысячи людей наблюдают за ними на берегу.', 1517563620, 1, '', 0, 'Анна Петяева');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (22659, NULL, 343, 'Анджей Зауха задал вопрос о крушении самолета президента Польши.', 1513251360, 3, '', 1, 'Илья Максимов');
