INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23019, NULL, 348, 'Супруга у Джордана внушительная такая. Настоящая валькирия (ну и что, что итальяно-еврейских кровей?). И, возможно, муза фильма "Прочь":
