INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23297, NULL, 350, 'Доброе утро! Через 20 минут на Красной площади Москвы начнется парад, посвященный 73-й годовщине Победы в Великой Отечественной войне.', 1525848000, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23298, NULL, 350, 'Погода в столице сегодня отличная, самолетам ВКС, похоже, даже не пришлось разгонять тучи. Красная площадь к параду уже готова - трибуны заполнены зрителями, колонны войск приготовились к началу торжественного марша, а на Тверской улице дожидается команды боевая техника.', 1525848300, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23605, NULL, 353, 'А еще у эстонки Нечаевой - красивое галлюциногенное платье', 1526153940, 1, '', 0, 'Дмитрий Сосновский');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23299, NULL, 350, 'В этом году в параде примут участие более тринадцати тысяч военных, 159 единиц военной техники, некоторые образцы которой будут показаны зрителям впервые, и 75 самолетов и вертолетов. Самым крупным был парад по случаю 70-летнего юбилея Победы, состоявшийся в 2015 году - тогда по Красной площади прошли 16,5 тысячи военнослужащих, проехали 193 единицы военной техники, в том числе новейшие танки, САУ и БМП. В воздушной части парада тогда приняли участие 143 самолета и вертолета.', 1525848660, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23300, NULL, 350, 'На Красную площадь прибыл президент Российской Федерации и Верховный главнокомандующий Вооруженными силами Владимир Путин. Он прошел на трибуну, поздоровался с военными и ветеранами.', 1525849020, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23301, NULL, 350, 'Куранты бьют 10 часов утра - на Красной площади начинается парад Победы.', 1525849200, 1, '', 0, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23302, NULL, 350, 'Под музыку песни "Священная война" знаменные группы батальона почетного караула 154-го отдельного комендантского Преображенского полка выносят на Красную площадь государственный флаг России и знамя Победы 1945 года.', 1525849260, 1, '', 1, 'Илья Максимов');
