INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23304, NULL, 350, 'Парадом командует главком Сухопутных войск генерал-полковник Олег Салюков. Парад принимает министр обороны РФ, Герой России, генерал армии Сергей Шойгу.', 1525849620, 1, '', 1, 'Илья Максимов');
