INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23341, NULL, 350, 'На Красной площади - сводный полк Воздушно-десантных войск. Парадный расчет курсантов Рязанского гвардейского высшего воздушно-десантного командного училища имени генерала армии Маргелова возглавляет гвардии генерал-майор Алексей Рагозин. За особые заслуги в деле подготовки офицерских кадров училище награждено орденом Суворова и двумя орденами боевого Красного Знамени. Среди выпускников училища 135 героев Советского Союза и России. Впервые в новейшей истории России учебному заведению за проявленные его выпускниками героизм, самоотверженность и мужество было присвоено почетное наименование "Гвардейское".  В этом году училище отмечает свой 100-летний юбилей.', 1525851480, 1, '', 1, 'Илья Максимов');
