INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23342, NULL, 350, 'Торжественным маршем по брусчатке проходят десантники 331-го гвардейского парашютно-десантного полка. Во главе расчета герой России гвардии полковник Владимир Селиверстов. Около 40 лет подряд 331-й гвардейский парашютно-десантный полк завоевывает право представлять ВДВ на парадах Победы. В годы войны личный состав полка участвовал в освобождеhttps://works.rg.ru/project/online_translations/?id_trans=350&section=1нии Венгрии, Австрии и Чехословакии. В полку свято чтут память о боевых подвигах ветеранов войны и берут с них пример. Полку присвоено почетное наименование "Ударный".', 1525851540, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23343, NULL, 350, 'Впервые в параде на Красной площади принимает участие парадный расчет военной полиции Вооруженных сил Российской Федерации.', 1525851600, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23344, NULL, 350, 'По главной площади страны проходит парадный расчет Военной академии радиационной, химической и биологической защиты имени маршала Тимошенко. Возглавляет расчет генерал-майор Игорь Емельянов. Войска РХБЗ, которые в этом году отмечают 100-летие со дня своего образования, в любой момент готовы организовать работы по ликвидации катастроф и их последствий на химически и биологически опасных объектах.', 1525851600, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23345, NULL, 350, 'На Красной площади - военнослужащие 38-й отдельной железнодорожной бригады. Парадный расчет возглавляет полковник Игорь Орлов. В годы войны воины-железнодорожники под огнем и бомбами врага обеспечивали бесперебойную доставку техники и материальных средств для нужд фронта. Сегодня железнодорожные войска продолжают вносить весомый вклад в развитие транспортной системы России.', 1525851660, 1, '', 1, 'Илья Максимов');
