INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23349, NULL, 350, 'Идет парадный расчет Московского пограничного института ФСБ России. Его возглавляет начальник института – генерал-майор Валерий Козлов. Пограничники надежно прикрывают границы Отечества от проникновения международного терроризма и трансграничной преступности.', 1525851780, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23350, NULL, 350, 'Войска Западного военного округа на главной площади страны представляют воины Московского гарнизона. По брусчатке проходит парадный расчет воинов-таманцев во главе с командиром 2-й гвардейской мотострелковой Таманской дивизии - полковником Евгением Никитиным. В годы войны 34 воина дивизии были удостоены звания героя Советского Союза, пять стали полными кавалерами ордена Славы. В составе парадного строя дивизии – прославленные орденоносные мотострелковые полки: Севастопольский, Шавлинский, Берлинский и танковый полк имени Катукова.', 1525851840, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23351, NULL, 350, 'На Красной площади - военнослужащие 4-й гвардейской танковой Кантемировской дивизии имени Андропова. В годы войны воинские части соединения награждены 23 орденами, личному составу корпуса 18 раз объявлялась благодарность Верховного Главнокомандующего, 57 военнослужащим присвоено звание Героя Советского Союза, пятеро воинов дивизии стали полными кавалерами ордена Славы.', 1525851900, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23352, NULL, 350, 'На марше парадный расчет 27-й отдельной гвардейской мотострелковой бригады во главе с ее командиром подполковником Александром Проценко. Ровно 74 года назад личный состав бригады вместе с другими частями штурмом овладели Сапун-горой и освободили Севастополь. За мужество и героизм, проявленные в боях, бригаде присвоено почетное наименование "Севастопольская".', 1525851900, 1, '', 1, 'Илья Максимов');
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23353, NULL, 350, 'Пешую часть парада завершают кремлевский курсанты. Это название прочно закрепилось за курсантами Московского высшего военного командного училища. Командует парадным расчетом начальник училища генерал-майор Александр Новкин. Из стен училища вышло более 700 генералов и маршалов. Более 100 выпускников училища носят высокое звание героев Советского Союза, России и социалистического труда.', 1525851960, 1, '', 1, 'Илья Максимов');
