INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23770, NULL, 354, 'Валерий Газзаев: Ваш вопрос о качестве нашего футбола я воспринял как поручение. Вместе с коллегами мы проработали этот вопрос. Просмотрели практически весь мировой футбол и разработали программу развития нашего чемпионата и футбола во всех регионах РФ. Что касается нашего вопроса. Безусловно, все наше футбольное сообщество понимает, какая у вас загруженность и надеется, что футбол останется в центре вашего внимания. Я хочу вам пожелать крепкого здоровья, чтоб вас хранили Святой Георгий и Николай Чудотворец.', 1528364640, 3, '', 1, 'Дмитрий Сосновский');
