INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23794, NULL, 354, 'Сергей Шаргунов, писатель, депутат: Приветствую вас из Приморья. Россия всегда была, вопреки всему, страной свободомыслия. В последнее время получаю много тревожных сигналов из провинции. Молодых патриотичных ребят осудили за создание группы по проведению референдума об ответственности власти в стране. Нет ли смысла приостановить тех, кто ведет подобные дела?', 1528366560, 3, '', 1, 'Илья Максимов');
