INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23810, NULL, 354, 'Владислав, Санкт-Петербург: Не секрет, что наша страна была одной из двух мировых держав, обладающей технологией повторного запуска космического корабля. Считаю космическую отрасль одной из важнейших для страны. Возможно ли возродить технологию "Бурана", какие в целом у России планы по расширению своего присутствия в космосе?', 1528368120, 3, '', 1, 'Илья Максимов');
