INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23817, NULL, 354, 'Очень много вопросов об онкологии. Во-первых, в регионах не хватает реабилитационных центров и квалифицированной помощи. Также - нехватка лекарств в больницах и "скорой помощи". Есть еще проблема неправильных диагнозов. Мы видим, что цепочка "пациент - врач - диагностика - лечение" не работает. Как нам решить эту проблему?', 1528368660, 3, '', 1, 'Дмитрий Сосновский');
