INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23820, NULL, 354, 'Струнино, Владимирская область: Недавно местную больницу присоединили к районной и лишили статуса самостоятельной единицы. Из-за этого ремонт не проводится уже очень давно. Уже закрыты хирургическое, детское и многие другие отделения. До ближайшей районной больницы нужно ехать 30 минут на электричке. Жители города создали инициативную группу, чтобы отстаивать свою больницу. 
INSERT INTO public.post (id, id_parent, id_broadcast, text, post_time, post_type, link, has_big_img, author) VALUES (23824, NULL, 354, 'Инициативная группа, Струнино, Владимирская область: Отделение не работает. Сказали, что его не закрывали, но оно не работает. Мы ездим в Александров. Скорую перевели в новое здание, но оно не соответствует нормам. Мы просим восстановить то, что разрушилось.', 1528369680, 3, '', 0, 'Илья Максимов');
